actions.action:
set_perm() {
	uid=$1; gid=$2; mod=$3;
	shift 3;
	chown $uid:$gid "$@" || chown $uid.$gid "$@";
	chmod $mod "$@";
}
set_perm_recursive() {
	uid=$1; gid=$2; dmod=$3; fmod=$4;
	shift 4;
	until [ ! "$1" ]; do
	chown -R $uid:$gid "$1" || chown -R $uid.$gid "$1";
	find "$1" -type d -exec chmod $dmod {} +;
	find "$1" -type f -exec chmod $fmod {} +;
	shift;
	done;
}
file_getprop() { grep "^$2" "$1" | head -n1 | cut -d= -f2; }
cp -a /etc/cookies /data;
set_perm_recursive 0 0 0755 0755 "/data/cookies";
set_perm_recursive 0 0 0755 0644 "/data/cookies/bin/avb" "/data/cookies/bin/chromeos";
set_perm 0 0 0644 "/data/cookies/authors.txt" "/data/cookies/bin/androidbootimg.magic" "/data/cookies/bin/BootSignature_Android.jar" "/data/cookies/bin/magic";	
cd /data/cookies;
cp /sdcard/SHRP/epicx/recovery.img /data/cookies/
sh unpackimg.sh;
rm -rf /data/cookies/ramdisk/twres/
cp -r /twres/ /data/cookies/ramdisk/

file_getprop() { grep "^$2" "$1" | head -n1 | cut -d= -f2; }
rm /data/cookies/ramdisk/twres/shrp_lock_data_base.xml
cp /twres/cache/shrp_lock_data_base.xml /data/cookies/ramdisk/twres/
cd /data/cookies;
sh repackimg.sh;
dd if=/data/cookies/image-new.img of=$(file_getprop /shrp_vital ro.shrp.recovery.block);
rm -r /data/cookies;

